const Box = styled.div&lt;{ $variant: &quot;primary&quot; | &quot;secondary&quot;, $color: string }&gt;`
  font-size: ${props =&gt; props.$variant === &quot;primary&quot; ? &quot;2rem&quot; : &quot;1rem&quot; };
  color: ${props =&gt; props.$color};
